mapper.select:

	select * from (
    select/*INDEX_DESC(board_ask BOARD_ASK_PK) */
    rownum rn,board_ask_number, board_ask_member_id, board_ask_date, board_ask_title, board_ask_content, board_ask_state, board_ask_type
    from board_ask
  	where rownum <= #{pageNum} * #{amount}
	)
	where rn > (#{pageNum} -1)* #{amount}
